import React from 'react';
import './styles.scss';
import {FaAngleUp} from 'react-icons/fa';
import {useMutation} from '@apollo/react-hooks';
import gql from 'graphql-tag';

const MODIFY_SCORE = gql`
  mutation ModifyPostScore($id: String!, $modifier: Int!) {
    modifyPostScore(id: $id, modifier: $modifier) {
      id
      score
    }
  }
`;

class Upvote extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      id: props.id,
      modifier: 1
    }

    this.upvote = this.upvote.bind(this);
  }


upvote(id){
    const [modifyScore] = useMutation(MODIFY_SCORE);
    return(
        <FaAngleUp className="socialCard-bottom-row-icon" 
            onClick={ e =>{
                e.preventDefault();
                modifyScore({variables: {id, modifier: 1}})
            }}
        />
    )
}


  render(){
    return this.upvote(this.state.id);
  };
};
export default Upvote;
