import React from 'react';
import {Modal, ModalHeader, ModalBody, ModalFooter, Button, Form, FormGroup, Input } from 'reactstrap';


class myModal extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      text: ""
    }
    this.toggleNewPost = this.toggleNewPost.bind(this);
  }

  toggleNewPost(){
    this.props.onChange();
  }

  render(){
    return(
        <Modal isOpen={this.props.open} toggle={this.toggleNewPost} className={this.props.className}>
            <ModalHeader toggle={this.toggleNewPost}>Add a new post</ModalHeader>

            <ModalBody>
              <Form>
                <FormGroup>
                  <Input type="textarea" name="text" id="exampleText"  onChange={(e) => this.setState({text: e.target.value})}>{this.state.value}</Input>
                </FormGroup>
              </Form>
            </ModalBody>

            <ModalFooter>
            <Button color="primary" onClick={this.toggleNewPost}>Post</Button>{' '}
            <Button color="secondary" onClick={this.toggleNewPost}>Cancel</Button>
            </ModalFooter>
        </Modal>
    )
  }
}
export default myModal;
