import React from 'react';
import './style.scss';
import Card from './Cards';

class CardManager extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      id: null,
      fetched: false
    }
  }

  componentDidMount(){
    fetch('https://xkcd.com/info.0.json')
    .then(response => response.json())
    .then(inData => this.setState({id: inData.num, fetched: true}));
  }

createCards(){
  let cards = []
  for(let i = 0; i < this.state.data.length; i++){
    cards.push(<Card id={this.state.id}/>);
  }
  return cards;
}


  render(){
    if(this.state.fetched){
      return(
        <div className="Cards">
        {this.createCards()}
        </div>
      )
    }else{
      return(
        <div className="Cards">
        Fetching posts
        </div>
      )
    }
  }
}
export default CardManager;
