import React from 'react';
import { Card, CardTitle, CardImg, CardSubtitle, CardText, Row, Col } from 'reactstrap';
import './styles.scss';
import {FaAngleUp, FaAngleDown} from 'react-icons/fa';
import Upvote from '../sub-components/Upvote';
import timeago from 'timeago.js';

class SocialCard extends React.Component{
  constructor(props){
    super(props);
    let dateIn = timeago.format(props.post.date)

    this.state = {
      id: props.post.id,
      text: props.post.text,
      date: dateIn,
      score: props.post.score
    }
  }


  render(){
    return(
      <Card className="socialCard">

        <Row className="socialCard-top-row">
          <Col>
            <CardText className="socialCard-date">{this.state.date}</CardText>
            <CardText>{this.state.id}</CardText>
          </Col>
        </Row>

        <Row className="socialCard-mid-row">
          <Col>
            <CardText>{this.state.text}</CardText>
          </Col>
        </Row>

        <Row className="socialCard-bottom-row">
          <Col className="socialCard-bottom-row-info"><Upvote id={this.state.is}/></Col>
          <Col className="socialCard-bottom-row-info">{this.state.score}</Col>
          <Col className="socialCard-bottom-row-info"><FaAngleDown className="socialCard-bottom-row-icon"/>{this.state.retweets}</Col>
        </Row>

      </Card>
    )
  }
}
export default SocialCard;
